import React, { useState } from 'react';
import image from './images/todo.png';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import ClearAllIcon from '@material-ui/icons/ClearAll';




const App = () =>{

    const [inputData, setInputData] = useState('');
    const [items, setItems] = useState([]);


    const addItems = () =>{
        if(!inputData){
            alert("Please Enter Data");
        }
        else{
        setItems([...items,inputData]);
        setInputData('');
        }
    }


    const deleteItem = () =>{
        
    }


    const inputEvent = (e) =>{
        setInputData(e.target.value)
    }


    return(
       <>
       <div className='main_div'>
           <div className='child_div'>
               <figure>
                   <img src={image} alt='todo_logo' height='80' width='80'/>
                <figcaption>Add Your List Here ✍️</figcaption>
               </figure>



               <div className='addItem'>
                    <input type='text' 
                        placeholder='Enter Your Todo List'
                        onChange={inputEvent} 
                        value={inputData}
                    />



                    <Button className='add-btn' title='add Item' onClick={addItems}>
                    <AddIcon />
                    </Button>
               </div>




               <div className='showItem'>
                   <Button className='dlt'>Remove all<ClearAllIcon/></Button>
               </div>

               <div className='show_item'>
              { items.map((elem,ind)=>{
                return(
                    <div className='each_item' key={ind}>
                     <DeleteIcon className='dlt' onClick={deleteItem}/>
                    <h3>{elem}</h3>
                   
                </div>
                )
               })}
                
               </div>
               
           </div>
       </div>
       </>
    )
}

export default App;
