import React, { useState } from 'react';
import todo from './images/todo.png'







const Todo = () => {


    const [inputData, setInputData] = useState('');
    const [items, setItems] = useState([]);


    const inputEvent = (e) => {
        setInputData(e.target.value);

    }




    // add item


    const addItem = () => {
        if (!inputData) {
            alert("Please enter data")
        }
        else {
            
            setItems([...items, inputData]);

            setInputData("");
        }
    }




    // delete item


    const deleteItem =(ind)=>{
        //console.log(index);

        const updatedItems = items.filter((elem)=>{
            return ind !== elem;
        })

        setItems(updatedItems)

    }





    // remove all

    const removeAll = () =>{
        setItems([])
    }




    return (
        <>
            <div className='main_div'>
                <div className='child_div'>
                    <figure>
                        <img src={todo} alt='todologo' width='70' height='70' />
                        <figcaption>Add Your List Here 👍</figcaption>
                    </figure>
                    <div className='addItems'>
                        <input type='text' placeholder='✍️ Add Item... '
                            value={inputData} onChange={inputEvent}
                        />


                        <i className="fas fa-plus add-btn" title='Add Item'
                            onClick={addItem}
                        />


                        <div className='showItem'>
                            <button className='btn effect04' data-sm-link-text='Remove All' onClick={removeAll}><span>Delete All</span></button>
                        </div>


                        <div className='showItem'>

                            {
                                items.map((elem,ind) => {
                                    return (
                                        <div className='eachItem' key={elem}>
                                            <div>
                                                <h3>{elem}</h3>
                                            </div>
                                            <i className="fas fa-trash dlt-btn" title='Delete Item' 
                                                onClick={()=>deleteItem(elem)}
                                            />
                                        </div>
                                    )
                                })
                            }



                        </div>



                    </div>


                    
                    
                </div>
            </div>
        </>
    )
}



export default Todo;
