import React, { useState } from 'react';
import image from './images/todo.png';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import ClearAllIcon from '@material-ui/icons/ClearAll';




const App = () =>{

    const [inputData, setInputData] = useState('');
    const [items, setItems] = useState(getLocalItmes());


    const addItems = () =>{
        if(!inputData){
            alert("Please Enter Data");
        }
        else{
            const allInputData = {id:new Date().getTime().toString(), name:inputData}
        setItems([...items,allInputData]);
        setInputData('');
        }
    }


    const deleteItem = (id) =>{
        const updatedItems = items.filter((elem,ind)=>{
            return ind !== id
        });

        setItems(updatedItems)
    }


    const removeAll = () =>{
        setItems([])
    }


    const inputEvent = (e) =>{
        setInputData(e.target.value)
    }


    return(
       <>
       <div className='main_div'>
           <div className='child_div'>
               <figure>
                   <img src={image} alt='todo_logo' height='80' width='80'/>
                <figcaption>Add Your List Here ✍️</figcaption>
               </figure>



               <div className='addItem'>
                    <input type='text' 
                        placeholder='Enter Your Todo List'
                        onChange={inputEvent} 
                        value={inputData}
                    />



                    <Button className='add-btn' title='add Item' onClick={addItems}>
                    <AddIcon />
                    </Button>
               </div>




               <div className='showItem'>
                   <Button className='dlt' onClick={removeAll}>Remove all<ClearAllIcon/></Button>
               </div>

               <div className='show_item'>
              { items.map((elem)=>{
                return(
                    <div className='each_item' key={elem.id}>
                     <DeleteIcon className='dlt' onClick={()=>deleteItem(elem.id)}/>
                    <h3>{elem.name}</h3>
                   
                </div>
                )
               })}
                
               </div>
               
           </div>
       </div>
       </>
    )
}

export default App;
